import React, { useState } from "react";
import Carousell from "../components/Carousell";
import LoadingSpinner from "../components/LoadingSpinner";

const Home = () => {
  const [shows, setShows] = useState([]);
  const [hasSearched, setHasSearched] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const url = "https://api.tvmaze.com/search/shows?q=";

  const fetchShow = async () => {
    setIsLoading(true);
    setError(null);

    try {
      const res = await fetch(url);
      const rawData = await res.json();
      console.log(rawData);

      // ===============================
      // Extract name and medium image from fetch result, set a placeholder image for shows without pics (otherwise will show broken image link)
      // <Reference from source code>
      //
      // a5 = 'Cat' && 'Dog'       // t && t returns "Dog"
      // a2 = true && false;       // t && f returns false
      // o5 = "Cat" || "Dog";      // t || t returns "Cat"
      // o6 = false || "Cat";      // f || t returns "Cat"
      // ===============================

      const filteredData = rawData.map((result) => {
        const { name, image, officialSite, summary, url } = result.show;
        return {
          name,
          image: (image && image.medium) || "https://i.imgur.com/V8olf7q.png",
          officialSite: officialSite || url,
          summary: summary || "Coming soon!",
        };
      });

      setShows(filteredData);
      console.log(shows);
    } catch (err) {
      setError(err.message);
    }

    setIsLoading(false);
  };

  return (
    <>
      <h1>Welcome to my Movie / TV Search App!</h1>
    </>
  );
};

export default Home;
