import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import Carousell from "./Carousell";
import Favourites from "./Favourites";

import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import ListGroup from "react-bootstrap/ListGroup";
import ListGroupItem from "react-bootstrap/ListGroupItem";

const Results = (props) => {
  // Favourite List
  const [favList, setFavList] = useState([]);

  const addFav = (e) => {
    setFavList((prevState) => {
      const newShow = document.getElementById(e.target.id);
      prevState.push(newShow.src);
      const newFav = prevState.map((data) => {
        return data;
      });
      return newFav;
    });
  };

  const removeFav = (e) => {
    setFavList((prevState) => {
      const targetId = e.target.parentElement.id;
      prevState.splice(targetId, 1);
      const newFav = prevState.map((data) => {
        return data;
      });
      return newFav;
    });
  };

  // ==================================================
  // Display results in Cards with the following info:
  // - Image
  // - Name
  // - Synopsis (within Accordian)
  // - Link to watch
  // ===================================================

  let results = props.shows.map((show, index) => {
    return (
      <Card
        className="card border-dark mb-3 mx-2"
        style={{ width: "17rem" }}
        key={uuidv4()}
      >
        <Card.Img
          variant="top"
          id={index}
          src={show.image}
          alt={show.name}
          onClick={addFav}
        />
        <Card.Body>
          <Card.Title>{show.name}</Card.Title>
          <Accordion>
            <Accordion.Item eventKey="0">
              <Accordion.Header>Synopsis</Accordion.Header>
              <Accordion.Body>{show.summary}</Accordion.Body>
            </Accordion.Item>
          </Accordion>
        </Card.Body>
        <Card.Footer>
          <ListGroup className="list-group-flush">
            <ListGroupItem>
              {" "}
              <p>Available on: </p>
              <a
                className="card-link"
                href="https://www.google.com/"
                target="_blank"
                rel="noreferrer"
              >
                Google
              </a>
              <a
                className="card-link"
                href="https://www.bbc.com/news"
                target="_blank"
                rel="noreferrer"
              >
                BBC News
              </a>
            </ListGroupItem>
          </ListGroup>
          <Card.Body>
            <button
              className="btn btn-danger"
              onClick={(e) => {
                e.preventDefault();
                window.open(show.officialSite);
              }}
            >
              Watch here
            </button>
          </Card.Body>
        </Card.Footer>
      </Card>
    );
  });

  return (
    <>
      <div className="backToSearch">
        <button onClick={props.onSearchAgain} className="btn btn-secondary">
          Back to Search
        </button>
        <br />
        <br />
      </div>

      <Carousell shows={props.shows} />

      <div className="row">{results}</div>

      <Favourites favList={favList} removeFav={removeFav} />
    </>
  );
};

export default Results;
