import React from "react";

const Results = (props) => {
  let results = props.shows.map((show, index) => {
    return (
      <div class="row" key={index}>
        <div class="col-sm-6">
          <div class="card-deck">
            <div
              className="card border-primary mb-3"
              style={{ width: "18rem" }}
            >
              <img className="card-image" src={show.image} alt={show.name} />
              <div className="card-body">
                <h5 className="card-title">{show.name}</h5>
                <p className="card-text">{show.summary}</p>
                <div class="card-footer">
                  <small class="text-muted">
                    <button
                      className="btn btn-primary"
                      onClick={(e) => {
                        e.preventDefault();
                        window.open(show.officialSite);
                      }}
                    >
                      Watch here
                    </button>
                  </small>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  });

  return (
    <>
      <div className="backToSearch">
        <button onClick={props.onSearchAgain} className="btn btn-danger">
          Back to Search
        </button>
        <br />
        <br />
      </div>
      <section className="cards">{results}</section>
    </>
  );
};

export default Results;
