import React, { useState } from "react";
import Search from "./Search";
import Results from "./Results";
import LoadingSpinner from "./LoadingSpinner";

const SearchContainer = () => {
  const [query, setQuery] = useState("");
  const [shows, setShows] = useState([]);
  const [hasSearched, setHasSearched] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // ====================================
  // Fetch shows from API based on query input
  // ====================================

  const url = "https://api.tvmaze.com/search/shows?q=";

  const fetchShow = async () => {
    const res = await fetch(url + query);
    const rawData = await res.json();
    console.log(rawData);

    // ===============================
    // Extract name and medium image from fetch result, set a placeholder image for shows without pics (otherwise will show broken image link)
    // <Reference from source code>
    //
    // a5 = 'Cat' && 'Dog'       // t && t returns "Dog"
    // a2 = true && false;       // t && f returns false
    // o5 = "Cat" || "Dog";      // t || t returns "Cat"
    // o6 = false || "Cat";      // f || t returns "Cat"
    // ===============================

    const filteredData = rawData.map((result) => {
      const { name, image } = result.show;
      return {
        name,
        image:
          (image && image.medium) ||
          "https://cdn.browshot.com/static/images/not-found.png",
      };
    });

    setShows(filteredData);
    console.log(shows);
  };

  // ===========================
  // 3 Functions to:
  // - handleSearchInput: manage changes to input field
  // - onSubmitQuery: initiate fetch request
  // - onSearchAgain: set state back to default (and show page with the search bar)
  // ===========================

  const handleSearchInput = (e) => {
    setQuery(e.target.value);
  };

  const onSubmitQuery = (e) => {
    e.preventDefault();
    if (query.length > 0) {
      fetchShow();
      setHasSearched(true);
      setQuery("");
    }
  };

  const onSearchAgain = () => {
    setHasSearched(false);
    setQuery("");
  };

  // =========================
  // Return
  // - Using ternary operator + hasSearched state to display either component
  // - hasSearched = false -> show page with Search bar / true -> show page with Results + "back to search" button)
  // =========================

  return (
    <div className="SearchContainer">
      {hasSearched ? (
        <section className="cards">
          <Results shows={shows} onSearchAgain={onSearchAgain} />
        </section>
      ) : (
        <Search
          handleSearchInput={handleSearchInput}
          onSubmitQuery={onSubmitQuery}
          query={query}
        />
      )}
    </div>
  );
};

export default SearchContainer;
