import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import Accordion from "react-bootstrap/Accordion";
import Carousell from "./Carousell";
import Favourites from "./Favourites";

const Results = (props) => {
  // Favourite List
  const [favList, setFavList] = useState([]);

  const addFav = () => {
    setFavList((prevState) => {
      const newShow = document.getElementById("0");
      prevState.push(newShow.src);
      const newFav = prevState.map((x) => {
        return x;
      });
      return newFav;
    });
  };

  const removeFav = (e) => {
    setFavList((prevState) => {
      const targetId = e.target.parentElement.id;
      prevState.splice(targetId, 1);
      const newFav = prevState.map((x) => {
        return x;
      });
      return newFav;
    });
  };

  // Cards

  let results = props.shows.map((show, index) => {
    return (
      <div
        className="card border-dark mb-3 mx-2"
        style={{ width: "17rem" }}
        key={uuidv4()}
      >
        <img
          className="card-image"
          id={index}
          src={show.image}
          alt={show.name}
          onClick={addFav}
        />

        <div className="card-body">
          <h5 className="card-title">{show.name}</h5>
          {/* <p className="card-text">{show.summary}</p> */}
          <Accordion>
            <Accordion.Item eventKey="0">
              <Accordion.Header>Synopsis</Accordion.Header>
              <Accordion.Body>{show.summary}</Accordion.Body>
            </Accordion.Item>
          </Accordion>
        </div>

        <div className="card-footer">
          <ul className="list-group list-group-flush">
            <li className="list-group-item">
              <p>Available on: </p>
              <a
                className="card-link"
                href="https://www.google.com/"
                target="_blank"
                rel="noreferrer"
              >
                Google
              </a>
              <a
                className="card-link"
                href="https://www.bbc.com/news"
                target="_blank"
                rel="noreferrer"
              >
                BBC News
              </a>
            </li>
          </ul>
          <div className="card-body">
            <button
              className="btn btn-danger"
              onClick={(e) => {
                e.preventDefault();
                window.open(show.officialSite);
              }}
            >
              Watch here
            </button>
          </div>
        </div>
      </div>
    );
  });

  return (
    <>
      <Carousell shows={props.shows} />

      <div className="backToSearch">
        <button onClick={props.onSearchAgain} className="btn btn-secondary">
          Back to Search
        </button>
        <br />
        <br />
      </div>

      <div className="row">{results}</div>

      <Favourites favList={favList} removeFav={removeFav} />
    </>
  );
};

export default Results;
