import React from "react";
import { v4 as uuidv4 } from "uuid";
import Accordion from "react-bootstrap/Accordion";

const Results = (props) => {
  let results = props.shows.map((show) => {
    return (
      <div
        className="card border-dark mb-3 mx-2"
        style={{ width: "17rem" }}
        key={uuidv4()}
      >
        <img className="card-image" src={show.image} alt={show.name} />
        <div className="card-body">
          <h5 className="card-title">{show.name}</h5>
          {/* <p className="card-text">{show.summary}</p> */}
          <Accordion>
            <Accordion.Item eventKey="0">
              <Accordion.Header>Synopsis</Accordion.Header>
              <Accordion.Body>{show.summary}</Accordion.Body>
            </Accordion.Item>
          </Accordion>
        </div>

        <div className="card-footer">
          <ul className="list-group list-group-flush">
            <li className="list-group-item">
              <p>Available on: </p>
              <a
                className="card-link"
                href="https://www.google.com/"
                target="_blank"
                rel="noreferrer"
              >
                Google
              </a>
              <a
                className="card-link"
                href="https://www.bbc.com/news"
                target="_blank"
                rel="noreferrer"
              >
                BBC News
              </a>
            </li>
          </ul>
          <div className="card-body">
            <button
              className="btn btn-danger"
              onClick={(e) => {
                e.preventDefault();
                window.open(show.officialSite);
              }}
            >
              Watch here
            </button>
          </div>
        </div>
      </div>
    );
  });

  return (
    <>
      <div className="backToSearch">
        <button onClick={props.onSearchAgain} className="btn btn-secondary">
          Back to Search
        </button>
        <br />
        <br />
      </div>
      <Carousel shows={props.shows} />

      <div className="row">{results}</div>
    </>
  );
};

export default Results;
