import React from "react";

const Results = (props) => {
  let results = props.shows.map((show, index) => {
    return (
      <div
        className="card border-primary mb-3 mx-2"
        style={{ width: "18rem" }}
        key={index}
      >
        <img className="card-image" src={show.image} alt={show.name} />
        <div className="card-body">
          <h5 className="card-title">{show.name}</h5>
          <p className="card-text">{show.summary}</p>
        </div>
        <div class="card-footer">
          <ul class="list-group list-group-flush">
            <li class="list-group-item">
              <p>Available on: </p>
              <a
                className="card-link"
                href="https://www.google.com/"
                target="_blank"
              >
                Link 1
              </a>
              <a
                className="card-link"
                href="https://www.bbc.com/news"
                target="_blank"
              >
                Link 2
              </a>
            </li>
          </ul>
          <div class="card-body">
            <button
              className="btn btn-danger"
              onClick={(e) => {
                e.preventDefault();
                window.open(show.officialSite);
              }}
            >
              Watch here
            </button>
          </div>
        </div>
      </div>
    );
  });

  return (
    <>
      <div className="backToSearch">
        <button onClick={props.onSearchAgain} className="btn btn-secondary">
          Back to Search
        </button>
        <br />
        <br />
      </div>

      <div className="row">{results}</div>
    </>
  );
};

export default Results;
