import React from "react";
import { v4 as uuidv4 } from "uuid";
import Card from "react-bootstrap/Card";

const Favourites = (props) => {
  console.log("props for Favourites: " + props.favList);

  let favMovieList = props.favList.map((url, index) => {
    return (
      <>
        <Card.Img variant="top" id={index} src={url} />
        <button onClick={props.removeFav} className="btn btn-danger">
          X
        </button>
      </>
    );
  });

  return (
    <div className="row">
      <h2>Favourites</h2>
      <p>Click on a poster to add to your favourites!</p>
      <Card
        className="card border-dark mb-3 mx-2"
        style={{ width: "16rem" }}
        key={uuidv4()}
        alt=""
      >
        {favMovieList}
      </Card>
    </div>
  );
};
export default Favourites;
