import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import Favourites from "./Favourites";

import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
// import ListGroup from "react-bootstrap/ListGroup";
// import ListGroupItem from "react-bootstrap/ListGroupItem";

const Results = (props) => {
  // Favourite List
  // Set: collection of unique values (no duplicates)

  const [favourites, setFavourites] = useState([]);

  const addFav = (e) => {
    setFavourites((prevState) => {
      const newShow = document.getElementById(e.target.id);
      prevState.push(newShow.src);

      // const deduped = [...new Set(prevState)];
      const deduped = Array.from(new Set(prevState));
      let data = deduped.filter(function (element) {
        return element !== undefined;
      });

      // console.log(data);
      return data;
    });
  };

  // Code to remove shows from Favourite List
  // gets image's ID based on clicked image -> use splice to remove it from Favourite List
  const removeFav = (e) => {
    setFavourites((prevState) => {
      const targetParentId = e.target.parentElement.id;
      prevState.splice(targetParentId, 1);

      // let data = prevState.filter(function (element) {
      //   return element !== undefined;
      // });

      // console.log(data);
      return favourites;
    });
  };

  // ==================================================
  // Display results in Cards with the following info:
  // - Image
  // - Name
  // - Synopsis (within Accordian)
  // - Link to watch
  // ===================================================

  let results = props.shows.map((show, index) => {
    return (
      <Card
        className="card border-dark mb-3 mx-2"
        style={{ width: "19rem" }}
        key={uuidv4()}
      >
        <Card.Img
          variant="top"
          className="cards"
          id={index}
          src={show.image}
          alt={show.name}
          onClick={addFav}
        />

        <Card.Body>
          <Card.Title>{show.name}</Card.Title>
          <Accordion>
            <Accordion.Item eventKey="0">
              <Accordion.Header>Synopsis</Accordion.Header>
              <Accordion.Body className="summary">
                {show.summary}
              </Accordion.Body>
            </Accordion.Item>
          </Accordion>
        </Card.Body>

        <Card.Footer className="footer">
          <>
            <Button
              variant="danger"
              onClick={(e) => {
                e.preventDefault();
                window.open(show.officialSite);
              }}
            >
              Watch here
            </Button>
          </>
        </Card.Footer>
      </Card>
    );
  });

  return (
    <>
      <Favourites favourites={favourites} removeFav={removeFav} />

      <div className="row">
        <h2>Search Results</h2>
        {results}
      </div>
    </>
  );
};

export default Results;
