import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import Favourites from "./Favourites";

import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
// import ListGroup from "react-bootstrap/ListGroup";
// import ListGroupItem from "react-bootstrap/ListGroupItem";

const Results = (props) => {
  // Favourite List
  const [favList, setFavList] = useState([]);

  // Code to add shows to Favourite List
  const addFav = (e) => {
    setFavList((prevState) => {
      const newShow = document.getElementById(e.target.id);
      prevState.push(newShow.src);
      // const favArray = prevState.map((data) => {
      //   return data;
      // });
      console.log(favArray);
      // return favArray;
    });
  };

  // Code to remove shows from Favourite List
  // gets image's ID based on clicked image -> use splice to remove it from Favourite List
  const removeFav = (e) => {
    setFavList((prevState) => {
      const targetId = e.target.parentElement.id;
      prevState.splice(targetId, 1);
      const favArray = prevState.map((data) => {
        return data;
      });
      return favArray;
    });
  };

  // ==================================================
  // Display results in Cards with the following info:
  // - Image
  // - Name
  // - Synopsis (within Accordian)
  // - Link to watch
  // ===================================================

  let results = props.shows.map((show, index) => {
    return (
      <Card
        className="card border-dark mb-3 mx-2"
        style={{ width: "19rem" }}
        key={uuidv4()}
      >
        <Card.Img
          variant="top"
          id={index}
          src={show.image}
          alt={show.name}
          onClick={addFav}
        />
        <Card.Body>
          <Card.Title>{show.name}</Card.Title>
          <Accordion>
            <Accordion.Item eventKey="0">
              <Accordion.Header>Synopsis</Accordion.Header>
              <Accordion.Body className="summary">
                {show.summary}
              </Accordion.Body>
            </Accordion.Item>
          </Accordion>
        </Card.Body>
        <Card.Footer className="footer">
          {/* <ListGroup className="list-group-flush">
            <ListGroupItem>
              <p>Available on: </p>
              <a
                className="card-link"
                href="https://www.google.com/"
                target="_blank"
                rel="noreferrer"
              >
                Google
              </a>
              <a
                className="card-link"
                href="https://www.bbc.com/news"
                target="_blank"
                rel="noreferrer"
              >
                BBC News
              </a>
            </ListGroupItem>
          </ListGroup> */}

          {/* <Card.Body> */}
          <>
            <Button
              variant="danger"
              onClick={(e) => {
                e.preventDefault();
                window.open(show.officialSite);
              }}
            >
              Watch here
            </Button>
          </>
          {/* </Card.Body> */}
        </Card.Footer>
      </Card>
    );
  });

  return (
    <>
      <Favourites favList={favList} removeFav={removeFav} />

      <div className="row">
        <h2>Search Results</h2>
        {results}
      </div>
    </>
  );
};

export default Results;
