import React from "react";

const Favourites = () => {
  // Favourite List

  const [favourites, setFavourites] = useState([]);

  const addFav = (e) => {
    setFavourites((prevState) => {
      const newShow = document.getElementById(e.target.id);
      prevState.push(newShow.src);

      const deduped = [...new Set(prevState)];
      // const deduped = Array.from(new Set(prevState));

      const favArray = deduped.map((data) => {
        return data;
      });
      console.log(deduped);
      return favArray;
    });
  };

  // Code to remove shows from Favourite List
  // gets image's ID based on clicked image -> use splice to remove it from Favourite List
  const removeFav = (e) => {
    setFavourites((prevState) => {
      const targetId = e.target.parentElement.id;
      prevState.splice(targetId, 1);
      const favArray = prevState.map((data) => {
        return data;
      });
      return favArray;
    });
  };

  return (
    <>
      <h1>Favourites</h1>
    </>
  );
};

export default Favourites;
