import React, { useState, useEffect } from "react";
import Carousell from "../components/Carousell";

const Home = () => {
  const [comingSoon, setComingSoon] = useState([]);
  const [trendingMovie, setTrendingMovie] = useState([]);
  const [trendingTv, setTrendingTv] = useState([]);

  const url = "https://imdb-api.com/en/API/ComingSoon/k_83ycln6p";

  // const fetchComingSoon = async () => {
  //   const res = await fetch(url);
  //   const rawData = await res.json();
  //   console.log(rawData.items);

  //   // ===============================
  //   // Extract name and medium image from fetch result, set a placeholder image for shows without pics (otherwise will show broken image link)
  //   // <Reference from source code>
  //   //
  //   // a5 = 'Cat' && 'Dog'       // t && t returns "Dog"
  //   // a2 = true && false;       // t && f returns false
  //   // o5 = "Cat" || "Dog";      // t || t returns "Cat"
  //   // o6 = false || "Cat";      // f || t returns "Cat"
  //   // ===============================

  //   const filteredData = rawData.items.map((result) => {
  //     const { title, fullTitle, image, plot, releaseState } = result;
  //     return {
  //       title,
  //       fullTitle,
  //       image,
  //       plot,
  //       releaseState,
  //     };
  //   });

  //   setComingSoon(filteredData);
  //   console.log(filteredData);
  // };

  //   //=========

  const url2 =
    "https://api.themoviedb.org/3/trending/movie/week?api_key=03c16b8a9560a4ea683a37f203c3e79f";

  const fetchPopularMovie = async () => {
    const res2 = await fetch(url2);
    const rawData2 = await res2.json();
    console.log(rawData2.results);

    const filteredData2 = rawData2.results.map((result) => {
      const { title, fullTitle, image, plot, releaseState } = result;
      return {
        title,
        fullTitle,
        image,
        plot,
        releaseState,
      };
    });
  };

  setTrendingMovie(filteredData2);
  console.log(filteredData2);

  //=========

  const url3 =
    "https://api.themoviedb.org/3/trending/tv/week?api_key=03c16b8a9560a4ea683a37f203c3e79f";

  const fetchPopularTv = async () => {
    const res3 = await fetch(url3);
    const rawData3 = await res3.json();
    console.log(rawData3.results);

    const filteredData3 = rawData3.results.map((result) => {
      const { title, fullTitle, image, plot, releaseState } = result;
      return {
        title,
        fullTitle,
        image,
        plot,
        releaseState,
      };
    });

    setTrendingTv(filteredData3);
    console.log(filteredData3);
  };

  //===========
  useEffect(() => {
    // fetchComingSoon();
    fetchPopularTv();
    fetchPopularMovie();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  return (
    <>
      <Carousell shows={comingSoon} />
      <br />
      <h1>Popular Movies</h1>
      <br />
      <h1>Popular TV Shows</h1>
    </>
  );
};

export default Home;
